mapper.update:
UPDATE firedevice
devicename = #{devicename}, manufacture = #{manufacture}, model = #{model}, productDate = #{productDate}, validate = #{validate}, setupDate = #{setupDate}, setLocation = #{setLocation}, siteid = #{siteid}, iDeviceType = #{iDeviceType}, memo = #{memo},
WHERE deviceNo = #{deviceNo}

mapper.select:
SELECT DATE_FORMAT(fd.SetupDate, '%Y/%m/%d') SetupDate, fd.SetLocation, s.siteid, s.sitename, fd.deviceNo, fd.devicename, fd.Manufacture, fd.model, DATE_FORMAT(fd.productDate, '%Y/%m/%d') productDate, DATE_FORMAT(fd.validate, '%Y/%m/%d') validate, fd.memo, t.iDeviceType, t.DeviceTypeName, ff.vSysdesc FROM site s INNER JOIN firedevice fd ON s.siteid = fd.siteid INNER JOIN devicetype t ON t.iDeviceType = fd.iDeviceType INNER JOIN firesystype ff ON t.tiSysType = ff.tiSysType WHERE orgid =#{orgid}
and ff.vSysdesc LIKE CONCAT('%',#{vSysdesc}, '%')
LIMIT #{startPos},#{pageSize}

SELECT fd.SetupDate, fd.SetLocation, s.siteid, s.sitename, fd.deviceNo, fd.devicename, fd.Manufacture, fd.model, fd.productDate, fd.validate, fd.memo, t.iDeviceType, t.DeviceTypeName, ff.vSysdesc FROM site s INNER JOIN firedevice fd ON s.siteid = fd.siteid INNER JOIN devicetype t ON t.iDeviceType = fd.iDeviceType INNER JOIN firesystype ff ON t.tiSysType = ff.tiSysType WHERE s.orgid =#{orgid}
and fd.deviceNo = #{deviceNo}
LIMIT #{startPos},#{pageSize}

SELECT TrainingID, TrainingTime, TrainingAddress, TrainingContent, TrainingObject, TrainingType, Lecturer, HowmanyPeople FROM training WHERE orgid = #{orgid}
and TrainingTime BETWEEN #{startTime} and #{endTime}
LIMIT #{startPos},#{pageSize}

SELECT manoeuvreID, manoeuvretime, address, Department, manager, content FROM manoeuvre WHERE orgid = #{orgid}
and manoeuvretime BETWEEN #{startTime} and #{endTime}
LIMIT #{startPos},#{pageSize}
