mapper.update:
UPDATE onlinefiresystem
tisystype = #{newTisystype}, states = #{states}, Remarks = #{Remarks}, YnOnline = #{YnOnline}, SysFlatpic = #{SysFlatpic},
WHERE tisystype = #{tisystype} AND siteid = #{siteid}

update flatpic
siteid = #{siteid}, floornum = #{floornum}, imFlatPic = #{imFlatPic},
where cFlatPic=#{cFlatPic}

UPDATE training
TrainingTime = #{trainingTime}, TrainingAddress = #{trainingAddress}, TrainingType = #{trainingType}, HowmanyPeople = #{howmanyPeople}, Lecturer = #{lecturer}, TrainingObject = #{trainingObject}, TrainingContent =#{trainingContent}, AttendPeople = #{attendPeople}, Examination =#{examination}, TrainingManager = #{trainingManager}, ContentFile =#{contentFile}, examfile = #{examfile}, signtable=#{signtable},
WHERE orgid = #{orgid} and TrainingID = #{trainingID}
