mapper.select:
SELECT count(*) FROM ( SELECT ul.UserCheckId, ul.UserCheckTime, ul.OrgUserId, ul.OrgManagerId, ul.SubmitTime, ul.Remarks, ul.SubmitStatet, ui.UserCheckResult FROM uerchecklist ul INNER JOIN usercheckinfo ui ON ul.userCheckId = ui.userCheckId
and ul.SubmitTime BETWEEN #{startDate} and #{endTime}
WHERE ul.orgid = #{orgID} ) n

SELECT ul.UserCheckId, ul.UserCheckTime, ul.OrgUserId orgUser, ul.OrgManagerId orgManager, ul.SubmitStatet, ul.SubmitTime, ul.Remarks FROM uerchecklist ul INNER JOIN usercheckinfo ui ON ul.userCheckId = ui.userCheckId WHERE ul.orgid = #{orgID}
and ul.UserCheckTime BETWEEN #{startDate} and #{endTime}
limit #{startPos},#{pageSize}

SELECT
count(*)
FROM
firesafetycheck
WHERE
orgid =#{orgid} 

and
CheckTime BETWEEN #{startTime}
and #{endTime}

SELECT FireSafetyCheckID, CheckTime, checkposite, Department, Problem, handing, attendperson, CheckedDepartment, RecordMan, SafetyMan FROM firesafetycheck WHERE orgid = #{orgid}
and CheckTime BETWEEN #{startTime} and #{endTime}
LIMIT #{startPos},#{pageSize}

mapper.update:
update firesafetycheck
CheckTime=#{checkTime}, checkposite=#{checkposite}, Department=#{department}, Problem=#{problem}, handing=#{handing}, attendperson=#{attendperson}, CheckedDepartment=#{checkedDepartment}, RecordMan=#{recordMan}, SafetyMan=#{safetyMan},
WHERE orgid = #{orgid} and FireSafetyCheckID=#{fireSafetyCheckID}
